import libnum
import uuid
flag="flag{"+str(uuid.uuid4())+"}"
m=libnum.s2n(flag)
p = libnum.generate_prime(2048)
q = libnum.generate_prime(2048)
x = libnum.generate_prime(4096)
y = libnum.generate_prime(4096)

n = p * q
e = 0x10001
c = pow(m, e, n)

print("c=",c)
print(x * p - y * q)
print("x=",x)
print("y=",y)

