import random
from Crypto.Util.number import *
import gmpy2
from flag import flag


flag = bytes_to_long(flag)
p = getPrime(512)
q = getPrime(512)
r = getPrime(512)
N = p*p*p*q*q*r
E = random.getrandbits(3) * N
assert E != 0
c = gmpy2.powmod(flag, E, N)
print(c)
print(N)
print(p)
print(r)
